const quantityInput=document.querySelector(".count-actions__quantity"),plusButtons=document.querySelectorAll(".count-actions__button--plus, .product-action-sticky__count-btn--plus"),minusButtons=document.querySelectorAll(".count-actions__button--minus, .product-action-sticky__count-btn--minus");function getQuantity(){let t=parseInt(quantityInput.textContent.trim())||1;return Math.max(1,t)}function setQuantity(t){t=Math.max(1,parseInt(t)||1),quantityInput.textContent=t}plusButtons.forEach(t=>{t.addEventListener("click",function(e){e.preventDefault();const n=getQuantity();setQuantity(n+1)})}),minusButtons.forEach(t=>{t.addEventListener("click",function(e){e.preventDefault();const n=getQuantity();n>1&&setQuantity(n-1)})}),quantityInput.addEventListener("blur",function(){let t=parseInt(this.textContent.trim())||1;this.textContent=Math.max(1,t)}),quantityInput.addEventListener("input",function(){this.textContent=this.textContent.replace(/[^0-9]/g,"")});
